selector .aux-grid-carousel-modern-layout .entry-main {
  min-height: auto;
  width: 85%;
  border-radius: 20px;
     background-color: #ffffff30;
    backdrop-filter: blur(10px);
}

selector .entry-title {
  position: relative;
  overflow: visible;
}

selector .entry-title:before {
  content: "";
  position: absolute;
  left: -18px;
  top: 50%;
  transform: translateY(-50%);
  width: 9px;
  height: 9px;
  border-radius: 50%;
  background-color: var(--e-global-color-519ca239);
}
selector .aux-grid-carousel-modern-layout .aux-col:hover .entry-main .entry-info:before,
selector .aux-grid-carousel-modern-layout .aux-col:hover .entry-main .entry-title:before {
  background-color: #fff;
}

selector .aux-grid-carousel-modern-layout .aux-media-frame {
  border-radius: 20px;
  box-shadow: 0 0 45px 0 rgba(0, 0, 0, 0.16);
}

selector .aux-grid-carousel-modern-layout .aux-col:hover .entry-main .entry-info a,
selector .aux-grid-carousel-modern-layout .aux-col:hover .entry-main .entry-title a{
  color: #FFF;
  position: relative;
  z-index: 9;
}

selector .aux-grid-carousel-modern-layout .aux-col:hover .entry-main:before {
  opacity: 1;
}

selector .aux-grid-carousel-modern-layout .aux-col .entry-main {
  position: relative;
  overflow: hidden;
}
selector .readmore{
    display: none;
}
selector .aux-grid-carousel-modern-layout .aux-col .entry-main:before {
  content: "";
  position: absolute;
  left: 0;
  top: 0;
  right: 0;
  bottom: 0;
  opacity: 0;
  transition: opacity 1200ms cubic-bezier(0.165, 0.84, 0.44, 1);
  box-shadow: 3px 14px 45px 0 rgba(0, 217, 166, 0.4);
     background-color: #FF7BB890;
    backdrop-filter: blur(10px);
}
