selector .column-entry{
    max-width: 635px;
    margin-left: auto;
    margin-right: auto;
}
}
@media only screen and (max-width: 440px) {
selector .aux-col {
       margin-top: -15%;
}
}

selector .widget-inner > section {
    margin-bottom: 15px;
    background-color: #FFFFFF;
    border-radius: 40px;
    position: relative;
    overflow: hidden;
}
selector .widget-inner > section:before{
    content: '';
    position: absolute;
    left: 0;
    top: 0;
    width: 100%;
    height: 100%;
}

selector h5:after{
    display: none;
}
selector section.active > h5:after {
    background-image: none;
    background-color: rgba(112, 112, 112, 0.36);
    display: block;
    width: auto;
    height: 1px;
    position: absolute;
    top: 98%;
    left: 4em;
    right: 2em; 
}
selector h5:before{
    content:'+';
    font-family: "Fira Sans" !important;
    font-style: normal !important;
    font-weight: 400 !important;
    line-height: 41px;
    position: absolute;
    left: 1%;
    top: 50%;
    transform: translateY(-50%);
    font-size: 34px;
    color: var(--e-global-color-ef5c86c);
    transition: transform 300ms;
    background: var(--auxin-featured-color-7);
    background: linear-gradient(180deg, var(--e-global-color-519ca239) 0%, var(--e-global-color-519ca239) 100%);
    padding: 9px 16px;
    border-radius: 50%;
}
selector section.active > h5:before {
    content: '-';
    color: #FFFFFF;
    background: #0A0A0A;
    background: linear-gradient(140deg, #49479A 0%, #49479A 100%);  
    padding: 9px 22px;
}

selector .acc-content-wrap {
    position: relative;
    z-index: 8;
}

selector .active:before{
    opacity: 1 !important;
}


selector .aux-toggle-header:focus {
    outline: 0;
background-color: var(--auxin-featured-color-7 );
}

selector .aux-toggle-content:focus {
    outline: 0;
}

@media only screen and (max-width:768px){
    selector h5:before {
        left: 4%;
    }
}
